sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */

	var c = this;


	//---------------------JSCHART---------------------------------------------------------------------------------------------------------------------------
	var ctx = document.getElementById('myChart');

	var totalVolume = 25978270006.99;
	var parcialVolume = 25000000001.20;
	var verifiableVolume = 25978270006.00;
	var notVerified = 978270005.00;
	var VerifiedPercent = 90;
	var teste = 978270005;



	new Chart(ctx, {
		type: 'bar',
		data: {
			labels: ['Volume Total', 'Parcial', 'Verifiable ', 'Not Verified', 'Verified Percent', 'teste'],
			datasets: [{
				label: 'Consolidate',
				data: [totalVolume, parcialVolume, verifiableVolume, notVerified, VerifiedPercent, teste],
				borderWidth: 1,				
				backgroundColor: [
					'rgba(255, 99, 132, 0.6)',
					'rgba(255, 159, 64, 0.2)',
					'rgba(255, 205, 86, 0.2)',
					'rgba(75, 192, 192, 0.2)',
					'rgba(54, 162, 235, 0.2)',
					'rgba(153, 102, 255, 0.2)',
					'rgba(201, 203, 207, 0.2)'
				],
				borderColor: [
					'rgb(255, 99, 132)',
					'rgb(255, 159, 64)',
					'rgb(255, 205, 86)',
					'rgb(75, 192, 192)',
					'rgb(54, 162, 235)',
					'rgb(153, 102, 255)',
					'rgb(201, 203, 207)'
				],

			},
								 {
									 type: 'bar',
									 label: 'Line Consolidate',
									 data: [totalVolume , parcialVolume, verifiableVolume, notVerified, VerifiedPercent, (teste * 2)],
								 }
								]
		},
		options: {
			indexAxis: 'y',
			scales: {
				y: {
					beginAtZero: false,
				}
			}
		}
	});

	//---------------------GOOGLECHART---------------------------------------------------------------------------------------------------------------------------
	google.charts.load("current", {packages:["corechart"]});
	google.charts.setOnLoadCallback(drawChart);
	function drawChart() {
		var data = google.visualization.arrayToDataTable([
			["Element", "Density", { role: "style" } ],
			["Copper", 8.94, "#b87333"],
			["Silver", 10.49, "silver"],
			["Gold", 19.30, "gold"],
			["Platinum", 21.45, "color: #e5e4e2"]
		]);

		var view = new google.visualization.DataView(data);
		view.setColumns([0, 1,
										 { calc: "stringify",
											sourceColumn: 1,
											type: "string",
											role: "annotation" },
										 2]);

		var options = {
			title: "GoogleChart",
			width: 600,
			height: 400,
			bar: {groupWidth: "95%"},
			legend: { position: "none" },
		};
		var chart = new google.visualization.BarChart(document.getElementById("barchart_values"));
		chart.draw(view, options);
	}
};
