sp_widget.client_script:
api.controller=function(spUtil) {
  /* widget controller */
  var c = this;

	window.onload = function() {
      var inputDate = document.getElementById('data');
      inputDate.placeholder = 'dd/mm/yyyy';
      inputDate.addEventListener('focus', function() {
        this.type = 'date';
        this.style.color = 'black'; // Para garantir que o texto seja visível
      });
      inputDate.addEventListener('blur', function() {
        if (!this.value) {
          this.type = 'text';
          this.style.color = 'gray'; // Cor do texto quando não há valor
          this.value = 'dd/mm/yyyy';
        }
      });
    };


	c.formatarData = function() {
  	spUtil.addInfoMessage("4 - Retornou algo do server");
		  var inputDate = document.getElementById('data').value;

      var partes = inputDate.split('-');
      var dataFormatada = partes[2] + '/' + partes[1] + '/' + partes[0];
      document.getElementById('data-formatada').innerText = "Data formatada: " + dataFormatada;



	}


};
