sp_widget.client_script:
api.controller = function ($scope, spUtil, $filter, $location) {


	/* é possível descobrir quais dependencias podem ser utilizadas 
	no angular buscando informação no site angular no <SERVUCE< */
	/* widget controller */



	var c = this;
	$scope.somesSysId = '68ea37be47b10a503cab8a88536d43e2';
	$scope.toUpperTitle = "Retornando UpperCase";
	$scope.onLoad = "Testando $filter(uppercase) no onload";
	$scope.locationURL = $location.search(); 




	$scope.onLoad = $filter("uppercase")($scope.onLoad)

	$scope.exibirMensagem = function () { 

		spUtil.addInfoMessage("teste");

	}

	$scope.transformarEmMaiusculo = function () {

		$scope.toUpperTitle = $filter("uppercase")($scope.toUpperTitle)

	}

};
